import React from 'react';
import { Map, Marker, Popup, TileLayer } from 'react-leaflet'

class LatLongMap extends React.Component {

	constructor(props) {
		super(props)
	  	this.state = {
	    	marker: props.marker
	    }
	}

	render() {
		console.log("Rendering marker: " + this.state.marker)
		const center = this.state.marker != null ? this.state.marker : [0, 0]
    	const markerElement = this.state.marker != null ? (
      		<Marker position={this.state.marker}>
        		<Popup>@ {this.state.marker[0]}, {this.state.marker[1]}</Popup>
      		</Marker>
    	) : null

    	return (
      		<Map ref={this.mapRef} center={center} zoom={8}>
	       		<TileLayer
	          		attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
	          		url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
	        	/>
	        	{markerElement}
      		</Map>
    	)
  	}
}

const LatLongList = ({latLongs, latLongMapRef}) => {
	var num = -1
	return <table>
		<tbody>
			{
				latLongs.map(latLong => {
				num = num + 1
				const lat = latLong.latitude
				const lon = latLong.longitude
				const handleClick = () => {
					console.log("You clicked item " + num + " @ " + lat + ", " + lon)
					latLongMapRef.current.setState({ marker : [lat, lon] })
				}
				return <tr key={lat + "," + lon}>
					<td onClick={handleClick}>{lat + ", " + lon}</td>
				</tr>
				}
			)}
		</tbody>
    </table>
}

const LatLongsComponent = ({latLongs}) => {
	// default marker is first marker in latLongs, or 0, 0 if latLongs is empty
	const marker = latLongs.length > 0 ? [latLongs[0].latitude, latLongs[0].longitude] : [0, 0]
	// allows access to actual LatLongMap
	const mapRef = React.createRef()
	const latLongMap = <LatLongMap ref={mapRef} marker={marker}/>
    return <table>
		<tbody>
			<tr>
				{ /* reference to actual LatLongMap is passed to LatLongList */ }
				<td><LatLongList latLongs={latLongs} latLongMapRef={mapRef}/></td>
				<td>{latLongMap}</td>
			</tr>
		</tbody>
    </table>
  }

export default LatLongsComponent;
